import React from "react";

const Dropdown = ({ options }) => {
  const renderedOptions = options.map((option) => {
    return(
      <div key={option.value} className="item">
        {option.label}
      </div>
    )
  })

  return <h1 style={{ color: `${textColor[0]}`}}>Dropdown</h1>
}


export default Dropdown;
