import React from "react";

import Dropdown from "./Dropdown";


const Translate = ({ languageOptions }) => {
  const renderedLanguages = languageOptions.map((language) => {
    return(
      <div
        key={language.value}
        className="item"
      >
        {language.label}
      </div>
    )
  })

  return(
    <div>
      <Dropdown options={renderedLanguages} />
    </div>
  )
};

export default Translate;
