import React from "react";

import Dropdown from "./Dropdown";

const languageOptions = [
  {
    label: 'Afrikaans',
    value: 'af'
  },
  {
    label: 'Arabic',
    value: 'ar'
  },
  {
    label: 'Hindi',
    value: 'hi'
  },
]


const Translate = ({ languageOptions }) => {
  const renderedLanguages = languageOptions.map((language) => {
    return(
      <div
        key={language.value}
        className="item"
      >
        {language.label}
      </div>
    )
  })

  return(
    <div>
      <Dropdown options={languageOptions} />
    </div>
  )
};

export default Translate;
